INSERT INTO `wp_posts` VALUES ('480', '1', '2015-11-06 08:03:33', '2015-11-06 08:03:33', 'Chúc mừng đến với WordPress. Đây là bài viết đầu tiên của bạn. Hãy chỉnh sửa hay xóa bài viết này, và bắt đầu viết blog!', 'Chào tất cả mọi người!', '', 'inherit', 'closed', 'closed', '', '1-revision-v1', '', '', '2015-11-06 08:03:33', '2015-11-06 08:03:33', '', '1', 'http://macca.local/2015/11/06/1-revision-v1/', '0', 'revision', '', '0');
